  <div class="form-group">
    <label for="email">Address:<span style="color:red">*</span></label>
    <textarea style="height:100px" class="form-control" id="add" required></textarea>
  </div>
  <div class="form-group">
  <div class="alert alert-warning" role="alert" id="pin_format">
